import React from 'react';
import { BrowserRouter as Router, Link } from "react-router-dom";
import products from '../data/members.json';

const Products = () => {

    const productData = products.map((product, index) => (
        <div key={index} className="categories">
          <div className="small-container">
            <div className="row">
              <div className="col-6">
                <img src={product.image} className="card-img" alt="..." />
              </div>
              <div className="col-6">
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                    <p className="card-text">{product.desc}</p>
                </div>
                  <p className="col-6">Click this link to go to my twitch...
                    <a className="nav-link" href={product.link} target="_blank">Twitch Link</a>
                  </p>
                  <button>Go to Product</button>
              </div>
            </div>
          </div>
        </div>
    ))
  
  return (
      <div>

        {productData}
        
      </div>
        

  );
};

export default Products;
