import React, { useRef } from "react";
import { firestore } from "../firebase";
import { addDoc, collection } from "@firebase/firestore";

export default function Signin() {
  const usersRef = useRef();
  const ref = collection(firestore, "users");

  const handleSave = async (e) => {
    e.preventDefault();
    console.log(usersRef.current.value);

    let data = {
      users: usersRef.current.value,
    };

    try {
      addDoc(ref, data);
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <div className="account-page">
      <div className="container">
        <div className="row">
          <div className="col-2">
            <img src="./assets/images/bigtires.png" width="100%" />
          </div>
          <form id="RegForm" onSubmit={handleSave}>
            <label for="firstName">First Name:</label>
            <input type="text" ref={usersRef} />
            <label for="lastName">Last Name:</label>
            <input type="text" ref={usersRef} />
            <label for="username">Username:</label>
            <input type="text" ref={usersRef} />
            <label for="email">Email:</label>
            <input type="text" ref={usersRef} />
            <label for="password">Password:</label>
            <input type="text" ref={usersRef} />
            <button type="submit" className="btn" value="Submit">
              Register
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
